import React from 'react';
import { Box, FormControl, InputLabel, MenuItem, Modal, Select, TextField, Typography } from '@mui/material';

const AddSchoolModal = ({open, onClose}) => {
    return (
        <div>
            <Modal open={open} onClose={handleClose} aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description">
                <Box sx={style} >
                    <Typography id="modal-modal-title" variant="h6" component="h2">
                        Add new School
                    </Typography>
                    <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                        <div style={{ display: 'inline-flex' }}>
                            <TextField id="outlined-basic" label="School name:" size="small" variant="outlined" ref="schoolName"/><br />
                            <div style={{ minWidth: 128 }}>
                                <TextField id="outlined-select-currency" select label="Select school type" ref="schoolType" sx={{ width: 190, marginLeft: 3 }}>
                                    {schoolType.map((option) => (
                                        <MenuItem key={option.value} value={option.value}>
                                            {option.label}
                                        </MenuItem>
                                    ))}
                                </TextField>
                            </div>
                        </div>
                        <TextField id="outlined-basic" label="Address:" size="small" variant="outlined" ref="schoolAddress" /><br />

                        <div style={{ width: 185 }}>
                            <Box sx={{ minWidth: 120 }}>
                                <FormControl fullWidth>
                                    <InputLabel>Select Area</InputLabel>
                                    <Select onChange={handleChangeIdSelected}>
                                        {listArea.map((item, id) => {
                                            return [
                                                <MenuItem value={item.areaId}>{item.areaName}</MenuItem>
                                            ]
                                        })}
                                    </Select>
                                </FormControl>
                            </Box>
                        </div>
                        <button style={{ marginLeft: 230, backgroundColor: "#04c23d" }} onClick={() => { addSchool() }}>Add</button>
                    </Typography>
                </Box>
            </Modal>
        </div>
    )
}

const addSchool = () => {
    console.log(this.refs.schoolName.getValue())
    console.log(this.refs.schoolType.getValue())
    console.log(this.refs.schoolAddress.getValue())
}

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};

const schoolType = [
    {
        value: '5',
        label: 'Tieu hoc',
    },
    {
        value: '9',
        label: 'Trung hoc Co sơ',
    },
    {
        value: '12',
        label: 'Trung hoc Pho thong',
    },
];

export default AddSchoolModal
