import React from 'react';
import "./tutor.scss";
import Modal from "./Modal";

const Card = ({ result, loading }) => {

  if (loading) {
    return <h2>Loading...</h2>
  }

  return (
    <div>
      {result.map((item) => {
        return [
          <div className="card cardTutor col-3">
            <div>
              <img src={item.imagePath} width="70" height="70" alt="Tutor image" style={{ marginBottom: 80 }} />
              <div className="header-right">
                <h5>{item.fullName}</h5>
                <p>
                  {item.email}<br />
                  {item.phoneNumber}
                </p>
              </div>
            </div>
            <button type="button" className="btnView" onClick={showDetail}>View all</button>
            
          </div>
        ]
      })}
    </div>
  );
}

const showDetail = () => {
  console.log("show modal");
  return (
    // <Modal />
    <div>
            <div>
                <div className="wrapper">
                    <a href="#demo-modal">Open Demo Modal</a>
                </div>
                <div id="demo-modal" className="modal">
                    <div className="modal__content">
                        <h1>CSS Only Modal</h1>
                        <p>
                            You can use the :target pseudo-class to create a modals with Zero JavaScript. Enjoy!
                        </p>
                        <div className="modal__footer">
                            Made with <i className="fa fa-heart" />, by <a href="https://twitter.com/denicmarko" target="_blank">@denicmarko</a>
                        </div>
                        <a href="#" className="modal__close">Ã—</a>
                    </div>
                </div>
            </div>
        </div>
  );
}

export default Card;
